const ChatBot = () => {
    // (Previous state and handlers remain the same)
  
    return (
      <div className="flex flex-col h-screen max-w-4xl mx-auto p-4">
        <div className="bg-white shadow-lg rounded-lg p-6 flex-1 flex flex-col">
          <h1 className="text-3xl font-bold mb-6">CDP Support Assistant</h1>
          
          <div className="flex-1 overflow-y-auto mb-4 space-y-4">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`p-4 rounded-lg ${
                  message.type === 'user'
                    ? 'bg-blue-100 ml-auto max-w-[80%]'
                    : 'bg-gray-100 mr-auto max-w-[80%]'
                }`}
              >
                <div className="whitespace-pre-wrap text-lg leading-relaxed">{message.text}</div>
                {message.additional_info && (
                  <div className="mt-4 text-base text-gray-600">
                    <p className="text-lg font-semibold">{message.additional_info.title}</p>
                    <p className="text-base">Platform: {message.additional_info.platform}</p>
                    <a
                      href={message.additional_info.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-blue-600 hover:underline inline-block mt-2 text-base"
                    >
                      View Full Documentation â†’
                    </a>
                  </div>
                )}
              </div>
            ))}
            {isLoading && (
              <div className="flex items-center justify-center">
                <RefreshCw className="animate-spin h-8 w-8 text-gray-500" />
              </div>
            )}
          </div>
  
          <form onSubmit={handleSubmit} className="flex gap-2">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask a question about Segment, mParticle, Lytics, or Zeotap..."
              className="flex-1 p-3 text-lg border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <button
              type="submit"
              disabled={isLoading}
              className="bg-blue-500 text-white p-3 rounded-lg hover:bg-blue-600 disabled:bg-gray-400"
            >
              <Send className="h-6 w-6" />
            </button>
          </form>
        </div>
      </div>
    );
  };
  
  export default ChatBot;
